    <div class=\"col-md-6\">
      <div class=\"card\">
        <div class=\"card-body\">
          <h1 class=\"card-title mb-4\">Créditer mon compte</h1>
          
          <div class=\"alert alert-info mb-4\">
            <strong>Solde actuel :</strong> {{ balance|number_format(2, ',', ' ') }} €
          </div>

          {% for label, messages in app.flashes %}
            {% for message in messages %}
              <div class=\"alert alert-{{ label }}\" role=\"alert\">{{ message }}</div>
            {% endfor %}
          {% endfor %}

          <form method=\"post\">
            <div class=\"mb-3\">
              <label for=\"amount\" class=\"form-label\">Montant (€)</label>
              <input type=\"number\" id=\"amount\" name=\"amount\" class=\"form-control\" 
                     min=\"1\" max=\"1000\" step=\"0.01\" required>
              <div class=\"form-text\">Montant entre 1€ et 1000€</div>
            </div>
            <button type=\"submit\" class=\"btn btn-brand w-100\">Créditer</button>
